$(document).ready(function(){var $cursor=$('.cursor');$(window).on('mousemove',function(e){var mouseX=e.clientX;var mouseY=e.clientY;$cursor.css({transform:`translate(${mouseX}px, ${mouseY}px)`})})});$(document).ready(function(){$(window).on('scroll',function(){var link=$('.asidebar a.dot');var top=$(window).scrollTop();$('.sec').each(function(){var id=$(this).attr('id');var height=$(this).height();var offset=$(this).offset().top-150;if(top>=offset&&top<offset+height){link.removeClass('dot-active');$('.asidebar').find('[data-scroll="'+id+'"]').addClass('dot-active')}})})});const validatableInputs=document.querySelectorAll('.valid-input');validatableInputs.forEach(input=>{input.addEventListener('input',function(){if(input.value!==''){input.classList.add('has-content')}else{input.classList.remove('has-content')}})})
